package com.example.helloworld;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a,b;
		
		a= 40;
				
		b = 50		;
		
		a= a+b;
		b= a-b;
		a = a-b;
		
		System.out.println("After swapping a ="  +a);
		System.out.println("After swapping b ="  +b);
		
		
		

	}

}
